import React from "react";
import JSONPretty from "react-json-pretty";
import { Button } from 'antd';
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
const axios = require("axios");
var JSONPrettyMon = require("react-json-pretty/dist/monikai");

class ReactUploadImage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
      data: []
    };
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  componentWillUpdate() {
    axios
      .get("http://localhost:3000/text")

      .then(res => {
        let d = res.data.filter(e => {
          delete e["_id"];
          delete e["__v"];
          return e;
        });
        let length = 0;
        let displayData = "";
        let e = d.forEach(e => {
          // console.log("Ra", e["image"].length)
          if ( e && e["image"] && e["image"].length > length) {
            displayData = e["image"];
          }
        });
        return this.setState({ data: displayData });
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      })
      .finally(function() {
        // always executed
      });
  }

  onFormSubmit(e) {
    e.preventDefault();
    const formData = new FormData();
    // this.state.data = []
    formData.append("myImage", this.state.file);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };
    axios
      .post("http://localhost:3000/text/upload", formData, config)
      .then(response => {
        alert("The file is successfully uploaded");
      })
      .catch(error => {});
    axios
      .get("http://localhost:3000/text")
      .then(res => this.setState({ data: res.data }))
      .catch(function(error) {
        // handle error
        console.log(error);
      })
      .finally(function() {
        // always executed
      });
  }

  onChange(e) {
    this.setState({ file: e.target.files[0] });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.onFormSubmit}>
          <h1>File Upload</h1>
          <input type="file" name="myImage" onChange={this.onChange} />
          {/* <button type="submit">Upload</button> */}
          <Button type="primary">Upload</Button>
        </form>
        {/* Data: {this.state.data.length > 0 && this.state.data.map(d=> JSON.stringify(d.image))} */}
        {this.state.data.length < 1 ? (
          <div></div>
        ) : (
          <div style={{width:"400px", display: "flex",
          alignItems: "center",height:"600px"}}>

            {this.state.data}
            {/* <JSONPretty
              themeClassName="custom-json-pretty"
              id="json-pretty"
              theme={JSONPrettyMon}
              data={JSON.stringify(this.state.data)}
            ></JSONPretty> */}
          </div>
        )}
      </div>
    );
  }
}

export default ReactUploadImage;
